import styled from "styled-components";

//background: url(${backgroundImg}) no-repeat center;
export const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;
  background: #f0f0f5;

  main {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 100%;
    max-width: 1100px;
    margin: 0 auto;
    padding: 0 2rem;
  }

  h1 {
    font-size: 3rem;
    color: #3a3a3a;
  }

  p {
    font-size: 1.5rem;
    color: #a8a8b3;
    margin-top: 0.5rem;
  }

  a {
    width: 100%;
    max-width: 700px;
    height: 150px;
    background: #04d361;
    border-radius: 8px;
    text-decoration: none;

    display: flex;
    align-items: center;
    overflow: hidden;
    margin-top: 3rem;

    &:hover {
      background: #04bf58;
    }

    div {
      display: flex;
      align-items: center;
      justify-content: center;
      width: 100%;
      height: 100%;
      background: rgba(0, 0, 0, 0.08);
      transition: background-color 0.2s;

      svg {
        width: 24px;
        height: 24px;
        color: #fff;
      }
    }
  }
`;
